mapper.update:
update condition
set user_id = #{userId,jdbcType=INTEGER},
  title = #{title,jdbcType=VARCHAR},
  intor = #{intor,jdbcType=VARCHAR},
  content = #{content,jdbcType=LONGVARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, user_id, title, intor, content
from condition
where id = #{id,jdbcType=INTEGER}
