mapper.insert:
insert into record (id, user_id, calorie, 
  distance, create_time, end_time
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{calorie,jdbcType=VARCHAR}, 
  #{distance,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select id, user_id, calorie, distance, create_time, end_time
from record
where id = #{id,jdbcType=INTEGER}

    select  user_id, Sum(calorie) as calorie, Sum(distance) as distance, date_format(create_time, '%Y-%m-%d') as time
    from record
    where user_id = #{id}
	GROUP BY date_format(create_time, '%Y-%m-%d')
	limit #{start}, #{size}
